package ch03.sec10;

public class AssignmentOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		System.out.println("result = " + result);
		result += 10;	//10
		System.out.println("result = " + result);
		result -= 5;	//5
		System.out.println("result = " + result);
		result *= 3;	//15
		System.out.println("result = " + result);
		result /= 3;	//5
		System.out.println("result = " + result);
		result %= 2;	//1
		System.out.println("result = " + result);
	}

}
